import urljoin from 'url-join';

const baseApiUrl = 'http://localhost:3000/api';
const basePublicUrl = 'http://localhost:3000/public';

export const API_URL = {
  DEPARTMENT: urljoin(baseApiUrl, 'department'),
  EMPLOYEE: urljoin(baseApiUrl, 'employee'),
  PROFILEPHOTO: urljoin(baseApiUrl, 'fileupload'),
  photosPath: urljoin(basePublicUrl, 'profile'),

  REGISTER: urljoin(baseApiUrl, 'register'),
  LOGIN: urljoin(baseApiUrl, 'login'),
};
